import React, { Component } from 'react'
import ReactCardFlip from 'react-card-flip'
import BackCard from './BackCard'
import Card from './Card';

export default class CardFlip extends Component {
    constructor(props) {
        super()
        this.state = {
            isFlipped: false
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(e) {
        e.preventDefault();
        this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
    }



    render() {
        return (
            <ReactCardFlip cardStyles = {{boxShadow: '4px 12px 40px 6px rgb(0 0 0 / 9%)'}} isFlipped={this.state.isFlipped} flipDirection="horizontal" containerStyle = {{width:"640px"}}>
                <Card onClick={this.handleClick} param = 'grmoney'>{this.props.description}</Card>

                <BackCard onClick={this.handleClick}>{this.props.BackCard_Description}</BackCard>
            </ReactCardFlip>
        )
    }
}
